    rep(i, 4) cin >> num[i];

    int ans[4] = {};
    int best = INF;
    static int coin[] = {10, 50, 100, 500};
    int idx[4] = {};
    repi(idx[3], num[3]) repi(idx[2], num[2]) repi(idx[1], num[1]) repi(idx[0], num[0]) {
      int ret = 0;
      rep(i, 4) ret += idx[i] * coin[i];
