				if(a[i] * cost[i] >= cost[j] && nes[j] > 0){
					long res = std::min(nes[j] * (cost[j] / cost[i]), a[i] / (cost[j] / cost[i]) * (cost[j] / cost[i]));
					ans += res;
					a[i] -= res;
					nes[j] -= res / (cost[j] / cost[i]);
				}
			}
			printf("%ld %ld\n", cost[i], ans);
		}
		printf("\n");
	}
}

