using namespace std;

int coin[4];
int coin_value[4] = {10,50,100,500};
int p;
int ans[4];
int ans_num;

void rec(int k) {
  static int i[4];
  if(k == 0) ans_num = 100000;

  if(k == 4) {
    int sum = 0;
    for(int j = 0; j < 4; j++) sum += coin_value[j] * i[j];
    if(sum < p) return;
    int otsuri_sum = sum - p;
    int otsuri[4];
    for(int j = 3; j >= 0; j--) {
      otsuri[j] = otsuri_sum / coin_value[j];
      otsuri_sum %= coin_value[j];
      if(i[j] > 0 && otsuri[j] > 0) return;
    }
    int num = 0;
    for(int j = 0; j < 4; j++) {
      num += (coin_value[j] - i[j]) + otsuri[j];
    }
    if(num < ans_num) {
      ans_num = num;
      for(int j = 0; j < 4; j++) {
	ans[j] = i[j];
      }
    }
    return;
  }

  for(i[k] = 0; i[k] <= coin[k]; i[k]++) {
    rec(k+1);
  }
}

main() {
  bool fFlag = false;
  while(1) {
    cin >> p;
    if(p == 0) break;

    for(int i = 0; i < 4; i++) cin >> coin[i];

    if(!fFlag) fFlag = true;
    else       cout << endl;

    rec(0);

    for(int i = 0; i < 4; i++) {
      if(ans[i] == 0) continue;
      cout << coin_value[i] << ' ' << ans[i] << endl;
    }
  }
}

