using namespace std;
int n,k[4],c[]={10,50,100,500},an[4],t[4],mm;
void dfs(int d,int s,int m){
  if(d==4){
    if(s>=n){
      int a=s-n;
      for(int i=3;i>=0;i--){
	  int p=a/c[i];
	  if(p&&t[i])m=1000000;
	  m+=p;
	  a%=c[i];
	}
      if(m<mm){
	mm=m;
	for(int i=0;i<4;i++)an[i]=t[i];
      }
    }
    return;
  }
  for(int i=0;i<=k[d];i++)
    t[d]=i,dfs(d+1,s+i*c[d],m+k[d]-i);
}

int main(){
  int y=0;
