using namespace std;

int pton(int p) {
	int cnt = 0;
	while ((p - 500) >= 0) {
		p -= 500;
		cnt++;
	}
	while ((p - 100) >= 0) {
		p -= 100;
		cnt++;
	}
	while ((p - 50) >= 0) {
		p -= 50;
		cnt++;
	}
	while ((p - 10) >= 0) {
		p -= 10;
		cnt++;
	}
	return cnt;
}

int main() {
	int flag = 0;
	while (1) {
		int i, j, k, l, a, b, c, d, ansa = 0, ansb = 0, ansc = 0, ansd = 0, minn = (1 << 20), p;
		if (flag == 1)cout << endl;
		cin >> p;
		if (p == 0)break;
		cin >> a >> b >> c >> d;

		for (i = 0; i <= a; i++) {
			for (j = 0; j <= b; j++) {
				for (k = 0; k <= c; k++) {
					for (l = 0; l <= d; l++) {
						if (10 * i + 50 * j + 100 * k + 500 * l >= p) {
							if (a - i + b - j + c - k + d - l + pton((10 * i + 50 * j + 100 * k + 500 * l) - p) < minn) {
								minn = a - i + b - j + c - k + d - l + pton((10 * i + 50 * j + 100 * k + 500 * l) - p);
								ansa = i;
								ansb = j;
								ansc = k;
								ansd = l;
							}
							break;
						}
					}
				}
			}
		}

		if (ansa > 0)cout << 10 << " " << ansa << endl;
		if (ansb > 0)cout << 50 << " " << ansb << endl;
		if (ansc > 0)cout << 100 << " " << ansc << endl;
		if (ansd > 0)cout << 500 << " " << ansd << endl;

		flag = flag | 1;
	}
	return 0;
}

