#include<stdio.h>
int F(int v)
{
	int r=v/500;
	r+=(v%=500)/100;
	r+=(v%=100)/50;
	r+=(v%=50)/10;
	return r;
}
int main()
{
	int a,b,c,d,i,j,k,l,p,q,r,s,u,v,w,m,x=0;;
	while(scanf("%d",&v),v)
	{
		scanf("%d%d%d%d",&a,&b,&c,&d);
		m=999;
		for(l=0;l<=d;++l)for(k=0;k<=c;++k)for(j=0;j<=b;++j)for(i=0;i<=a;++i)
		{
			u=i*10+j*50+k*100+l*500;
			if(u>=v)
			{
				w=F(u-v);
				if(m > a+b+c+d-i-j-k-l+w)
				{
					m=a+b+c+d-i-j-k-l+w;
					p=i;
					q=j;
					r=k;
					s=l;
				}
			}
		}
		if(x++)puts("");
		if(p)printf("10 %d\n",p);
		if(q)printf("50 %d\n",q);
		if(r)printf("100 %d\n",r);
		if(s)printf("500 %d\n",s);
	}
	return 0;
}

