#define N 194481
enum coins{ ten = 0, fifty, hundred, five_hundred, coin_max };
const int kcoin[coin_max] = {10,50,100,500};

int money_table[N][6];

inline int minimumExchange(int m)
{
	int t = 0;
	int ans = 0;

	t = m / 500;
	m -= 500 * t;
	ans += t;

	t = m / 100;
	m -= 100 * t;
	ans += t;

	t = m / 50;
	m -= 50 * t;
	ans += t;

	t = m / 10;
	m -= 10 * t;
	ans += t;

	return ans;
}

int main(){
