	for(int i=0;i<N;++i){
		if(i>0&&i%21==0){ num = -1; }
		if(i>0){ ++num; }
		money_table[i][0] += kcoin[coin] * num;
		money_table[i][1] += num;
		money_table[i][5] = num;
	}

	while(true){
		int M;
		int num_total = 0;
		int nums[coin_max] = {0,};
		int max_diff = 0;
		int ans_total = 0;
		int ans_nums[coin_max] = {0,};

		scanf("%d", &M);
