const int MAX = 200000;
const double PI = 3.14159265358979323846;
const double EPS = 1e-12;
const int INF = 1<<29;
const int MOD = 1000000007;

const int coins[] = {10, 50, 100, 500};
int cnt[4], coin[4];

void solve(void){
    int N;
    while (1) {
        cin >> N;
        if (N == 0) break;
        rep(4, i) cin >> coin[i];
        rep(3, c) {
            cnt[c] = min((N%coins[c+1])/coins[c], coin[c]);
            N -= coins[c]*cnt[c];
        }
        cnt[3] = N/coins[3];
        N -= coins[3]*cnt[3];
        rep(4, i) {
            if (N <= 0) break;
            if (cnt[i] == coin[i]) continue;
            cnt[i] += (N-1) / coins[i] + 1;
            N -= ((N-1) / coins[i] + 1) * coins[i];
        }
        rep(4, i) {
            if (cnt[i]) printf("%d %d\n", coins[i], cnt[i]);
        }

        printf("\n");
    }
}

int main(void){
  solve();
  return 0;
}

