void dfs(int i){
	if(i==4){
		int sum=0;
		rep(j,4) sum+=coin[j]*index[j];
		if(sum<t)
			return;
		sum-=t;
		int ch[4];
		rep(j,4){
			ch[3-j]=sum/coin[3-j];
			sum-=(sum/coin[3-j]*coin[3-j]);
		}
		int cur=0;
		rep(j,4) cur+=(num[j]-index[j]+ch[j]);
		if(cur<cmin){
			cmin=cur;
			rep(j,4) res[j]=index[j];
		}
		return;
	}
	for(index[i]=0;index[i]<=num[i];++index[i]){
		dfs(i+1);
	}
	return ;
}

