#include <iostream>
#include <map>
#include <utility>

using namespace std;

int inf = 99999;

int vals[] = {500, 100, 50, 10};

int return_cost(int total, map<int, int> &payment) {
    int total_payment = 0;
    int cost = 0;
    for (map<int, int>::iterator it = payment.begin(); it != payment.end(); ++it) {
        total_payment += it->first * it->second;
        cost -= it->second;
    }
    // cerr << "TOT " << total_payment << endl;
    int returns = total_payment - total;
    if (returns < 0) {
        return inf;
    }

    for (size_t i = 0; i < 4; ++i) {
        cost += int(returns / vals[i]);
        returns %= vals[i];
    }

    return cost;
}

pair<map<int, int>, int> find_payment(int total, map<int, int> &n, int i, map<int, int> &sofar) {
    if (i >= 4) {
        return make_pair(sofar, return_cost(total, sofar));
    }

    int val = vals[i];

    map<int, int> min_pays;
    int min_cost = inf;
    for (int k = 0; k <= n[val]; ++k) {
        map<int, int> pays(sofar);
        pays[val] = k;

        pair<map<int, int>, int> pa = find_payment(total, n, i+1, pays);
        map<int, int> pays_ = pa.first;
        int cost = pa.second;
        if (cost < min_cost) {
            min_pays = pays_;
            min_cost = cost;
        }
    }

    return make_pair(min_pays, min_cost);
}

void solve(int total, map<int, int> &n) {
    map<int, int> sofar;
    pair<map<int, int>, int> pa = find_payment(total, n, 0, sofar);
    map<int, int> pays = pa.first;
    for (int i = 3; i >= 0; --i) {
        int val = vals[i];
        if (pays[val] > 0) {
            cout << val << " " << pays[val] << endl;
        }
    }
    cout << endl;
}

int main() {
    while (1) {
        int total;
        map<int, int> n;

        cin >> total;
        if (total == 0) break;

        cin >> n[10] >> n[50] >> n[100] >> n[500];
        solve(total, n);
    }
}

