#include <bits/stdc++.h>

using namespace std;

const int MAX = 20 * 20 * 20 * 20;
int coin[4] = {10, 50, 100, 500};

int change(int a, int b, int c, int d, int p) {
  int sum = 10 * a + 50 * b + 100 * c + 500 * d - p;
  if (sum < 0) return MAX + 1;

  int res = 0;
  for (int i = 3; i >= 0; i--) {
    res += sum / coin[i];
    sum %= coin[i];
  }

  return res;
}

int main(void) {
  int p;
  bool flag = false;
  while (cin >> p, p) {
    if (flag) cout << endl;

    vector<int> c(4);
    int sumc = 0;
    for (int i = 0; i < 4; i++) {
      cin >> c[i];
      sumc += c[i];
    }

    int minc = MAX + 1;
    int ans[4] = {};
    for (int i = 0; i <= c[0]; i++) {
      for (int j = 0; j <= c[1]; j++) {
        for (int k = 0; k <= c[2]; k++) {
          for (int l = 0; l <= c[3]; l++) {
            int payc = i + j + k + l;
            if (sumc - payc + change(i, j, k, l, p) < minc) {
              ans[0] = i, ans[1] = j, ans[2] = k, ans[3] = l;
              minc = sumc - payc + change(i, j, k, l, p);
            }
          }
        }
      }
    }

    for (int i = 0; i < 4; i++) {
      if (ans[i]) {
        cout << coin[i] << " " << ans[i] << endl;
      }
    }

    flag = true;
  }

  return 0;
}

