		for(int i = 0; i <= a[0]; i++){
			for(int j = 0; j <= a[1]; j++){
				for(int k = 0; k <= a[2]; k++){
					for(int l = 0; l <= a[3]; l++){
						int m = i+j*5+k*10+l*50-n;
