						int cnt = 0;
						//cout << m << endl;
						if(l > 0 && m/50 > 0) continue;
						cnt += (m/50);
						m %= 50;
						if(k > 0 && m/10 > 0) continue;
						cnt += (m/10);
						m %= 10;
						if(j > 0 && m/5 > 0) continue;
						cnt += (m/5);
						m %= 5;
						if(i > 0 && m > 0) continue;
						cnt += m;
						int tmp = a[0]+a[1]+a[2]+a[3]-i-j-k-l+cnt;
						//cout << tmp  << " " << cnt << " " << i << " " << j << " " << k << " " << l << endl;
						if(ans > tmp){
							ans = tmp;
							out[0] = i;
							out[1] = j;
							out[2] = k;
							out[3] = l;
						}
					}
				}
			}
		}
		for(int i = 0; i < 4; i++){
			if(out[i] == 0) continue;
			printf("%d %d\n", num[i],out[i]);
		}
		printf("\n");
	}
}

