int main(void)
{
	int co[4]={10,50,100,500};
	int kin,mai[4],harai,ma2;
	int i,j,k,ii,i2,j2,k2,ii2,cnt1,cnt2,mn;
	int mi[4];
	int flg;
	flg=1;
	while(1)	{
		scanf("%d",&kin);
		if(kin==0)	break;
		cnt1=0;
		for(i=0;i<4;i++)	{
			scanf("%d",&mai[i]);
			cnt1+=mai[i];
		}
		mn=10000000;
		for(i=0;i<=mai[0];i++)	{
			for(j=0;j<=mai[1];j++)	{
				for(k=0;k<=mai[2];k++)	{
					for(ii=0;ii<=mai[3];ii++)	{
						harai=i*co[0]+j*co[1]+k*co[2]+ii*co[3];
//						printf("harai=%d i=%d j=%d k=%d ii=%d\n",harai,i,j,k,ii);

						harai-=kin;
						if(harai<0)	continue;
						ii2=harai/co[3];
						harai%=co[3];
						k2=harai/co[2];
						harai%=co[2];
						j2=harai/co[1];
						i2=harai%co[1];
						ma2=cnt1-i-j-k-ii+i2+j2+k2+ii2;
						if(ma2<mn)	{
							mn=ma2;
							mi[0]=i-i2;	mi[1]=j-j2;	mi[2]=k-k2;	mi[3]=ii-ii2;
