int check(int ind) {
	if ((ind%2) == 0) {
		return 10 * pow(10,(int)(ind/2));
	}
	else {
		return 50 * pow(10,(int)(ind/2));
	}
}

bool can_ex(std::vector<int> v, std::vector<int> m, int ind) {
	if (ind == 3) {
		return false;
	}

	if (v[ind+1] == 0) {
		return true;
	}
	else {
		if (v[3] > 0) {
			return false;
		}

		int change;
		if (ind == 0) {
			change = m[ind]/5;
			if (change+m[ind+1] > 1) {
				return true;
			}
		}
		if (ind == 1) {
			change = m[ind]/2;
			if (change+m[ind+1] > 4) {
				return true;
			}
		}
	}

	return true;
}


