vector<pair<int,int> > solve(){
  vector<pair<int,int> > res;
  vector<int> ans;
  ans.resize(4);
  for(int i=0;i<4;i++){
    int tmp=pay%toC(i+1);
    if(c[i]*toC(i)<tmp){
      ans[i+1]+=1;
      pay-=tmp;
    }else{
      ans[i]+=tmp/toC(i);
      pay-=tmp;
    }
  }
  c[1]+=(c[0]/5);
  ans[0]+=c[0]/5;
  c[0]-=c[0]/5;
  c[2]+=(c[1]/2);
  ans[1]+=c[1]/2;
  c[1]-=c[1]/2;
  c[3]+=(c[3]/5);
  ans[2]+=c[2]/5;
  c[2]-=c[2]/5;
  for(int i=0;i<4;i++){
    if(ans[i]!=0){
      res.push_back(make_pair(toC(i),ans[i]));
    }
  }
  sort(res.begin(),res.end());
  return res;
}

