      break;

    if (!first) {
      cout << endl;
      first = false;
    }

    uint32_t c10, c50, c100, c500;
    cin >> c10 >> c50 >> c100 >> c500;

    uint32_t total = c10 + c50 + c100 + c500;
    uint32_t min_c10 = 0;
    uint32_t min_c50 = 0;
    uint32_t min_c100 = 0;
    uint32_t min_c500 = 0;
