              change %= 500;
              uint32_t r100 = change / 100;
              change %= 100;
              uint32_t r50 = change / 50;
              change %= 50;
              uint32_t r10 = change / 10;
              uint32_t remain = c10 - i + r10 + c50 - j + r50 + c100 - k +
                                r100 + c500 - l + r500;
              if (remain < min_remain) {
                min_remain = remain;
                min_c10 = i;
                min_c50 = j;
                min_c100 = k;
                min_c500 = l;
              }
            }
          }
        }
