		money_table[i][1] += num;
		money_table[i][3] = num;
	}

	num = 0;
	coin = hundred;
	for(int i=0;i<N;++i){
		if(i>0&&i%441==0){ num = -1; }
		if(i>0&&i%21==0){ ++num; }
		money_table[i][0] += kcoin[coin] * num;
		money_table[i][1] += num;
		money_table[i][4] = num;
	}

