using namespace std;
using ll = long long;
vector<ll> coin = {10, 50, 100, 500};

ll getOturi(ll diff) {
  vector<ll> res(4);
  for (int i = 3; i >= 0; --i) {
    res[i] = diff / coin[i];
    diff -= res[i] * coin[i];
  }

  ll ans = 0;
  for (auto &&e : res) {
    ans += e;
  }
  return ans;
}

void solve(ll N) {
  vector<ll> ipt(4);

  for (auto &&e : ipt) {
    cin >> e;
  }

  ll oturi = numeric_limits<ll>::max();

  vector<ll> res(4, 0);
  for (int i = 0; i <= ipt[0]; ++i) {
    for (int j = 0; j <= ipt[1]; ++j) {
      for (int k = 0; k <= ipt[2]; ++k) {
        for (int l = 0; l <= ipt[3]; ++l) {
          ll val = coin[0] * i + coin[1] * j + coin[2] * k + coin[3] * l;
          if (val >= N) {
            ll diff = val - N;
            ll curOturi = getOturi(diff) - i - j - k - l;
            if (curOturi < oturi) {
              oturi = curOturi;
              res = {i, j, k, l};
            }
          }
        }
      }
    }
  }

  for (int i = 0; i < 4; ++i) {
    if (res[i] > 0) {
      cout << coin[i] << ' ' << res[i] << endl;
    }
  }
}

int main() {
  ll N;
  bool flag = false;
  while (cin >> N and N > 0) {
    if (flag) {
      cout << endl;
    }
    solve(N);
    flag = true;
  }
}

