struct Coin{
	Coin() {};
	Coin(int fh, int hd, int fy, int tn) : fh(fh), hd(hd), fy(fy), tn(tn){
	}
	int fh; //500
	int hd;	//100
	int fy;	//50
	int tn;	//10
	int calcNum(){
		return fh + hd + fy + tn;
	}
	int calcSum(){
		return fh * 500 + hd * 100 + fy * 50 + tn * 10;
	}
	Coin operator - (Coin c) {
		return Coin(fh - c.fh, hd - c.hd, fy - c.fy, tn - c.tn);
	}
	Coin operator + (Coin c) {
		return Coin(fh + c.fh, hd + c.hd, fy + c.fy, tn + c.tn);
	}
};

Coin GetChage(Coin pay, int price) {
	int chage = pay.calcSum() - price;
	Coin res(0, 0, 0, 0);

	while (chage / 500) {
		res.fh++;
		chage -= 500;
	}
	while (chage / 100) {
		res.hd++;
		chage -= 100;
	}
	while (chage / 50) {
		res.fy++;
		chage -= 50;
	}
	while (chage / 10) {
		res.tn++;
		chage -= 10;
	}

	return res;
}

int main(void) {
	int price;
	cin >> price;
	while (price) {
		Coin minAfterHold(20, 20, 20, 20);
		Coin hold;
		cin >> hold.tn >> hold.fy >> hold.hd >> hold.fh;

		// pay
		for (int fh = 0; fh <= hold.fh; fh++) {
			for (int hd = 0; hd <= hold.hd; hd++) {
				for (int fy = 0; fy <= hold.fy; fy++) {
					for (int tn = 0; tn <= hold.tn; tn++) {
						Coin Pay(fh, hd, fy, tn);
						if (price <= Pay.calcSum()) {
							Coin chage =  GetChage(Pay, price);
							Coin after = hold - Pay + chage;
							if (minAfterHold.calcNum() > after.calcNum()) {
								minAfterHold = after;
							}
						}
					}
				}
			}
		}

		// output
		Coin bestPay = hold - minAfterHold;
		if (bestPay.tn > 0) {
			printf("10 %d\n", bestPay.tn);
		}
		if (bestPay.fy > 0) {
			printf("50 %d\n", bestPay.fy);
		}
		if (bestPay.hd > 0) {
			printf("100 %d\n", bestPay.hd);
		}
		if (bestPay.fh > 0) {
			printf("500 %d\n", bestPay.fh);
		}

		cin >> price;
		if(price!=0){
			cout << endl;
		}
	}


	return 0;
}

