      if(s >= N) {
        int change = s - N;

        int chd = change / 500;
        change -= chd * 500;
        int chc = change / 100;
        change -= chc * 100;
        int chb = change / 50;
        change -= chb * 50;
        int cha = change / 10;
        change -= cha * 10;

        int ka = A - a + cha;
        int kb = B - b + chb;
        int kc = C - c + chc;
        int kd = D - d + chd;

        if(n > ka + kb + kc + kd) {
          aa = a, ab = b, ac = c, ad = d;
          n = ka + kb + kc + kd;
        }
      }
    }

    if(!first) cout << endl;
    first = false;

    if(aa > 0) cout << "10 " << aa << endl;
    if(ab > 0) cout << "50 " << ab << endl;
    if(ac > 0) cout << "100 " << ac << endl;
    if(ad > 0) cout << "500 " << ad << endl;
  }

  return 0;
}

