const int k = 4;
const int t[k] = { 10, 50, 100, 500 };

vector<int> clerk(int change) {
	vector<int> coins(k);

	for(int i = 3; i >= 0; i--) {
		coins[i] = change / t[i]; 
		change -= t[i] * coins[i];
	}

	return coins;
}

