	REP(i,4) cin >> num[i];
	if(flag) cout << endl;
	res = -1;
	dfs(0);
	REP(i,4)
	  if(ans[i] > 0) cout << M[i] << " " << ans[i] << endl;
	flag = true;
  }

  return 0;
}

