const ll MOD = 1000000007;

VI calc(int n)
{
    VI ret(4, 0);
    ret[3] = n / 500;
    n %= 500;
    ret[2] = n / 100;
    n %= 100;
    ret[1] = n / 50;
    n %= 50;
    ret[0] = n / 10;
    n %= 10;
    return ret;
}

VI ans;
int cost = IINF;
int value[4] = {10, 50, 100, 500};
int n;
void dfs(VI &curnum, int depth, VI &num)
{
    if (depth == 4)
    {
        int cur = 0;
        int curcost = 0;
        rep(i, 4)
        {
            cur += value[i] * curnum[i];
        }
        if (cur >= n)
        {
            auto ret = calc(cur - n);
            int curcost = 0;
            rep(i, 4)
            {
                curcost += ret[i] + num[i] - curnum[i];
                if (curnum[i] != 0 && ret[i] != 0)
                {
                    curcost = IINF;
                    break;
                }
            }
            if (curcost < cost)
            {
                cost = curcost;
                ans = curnum;
            }
        }
    }
    else
    {
        curnum.push_back(0);
        dfs(curnum, depth + 1, num);
        rep(i, num[depth])
        {
            curnum.back()++;
            dfs(curnum, depth + 1, num);
        }
        curnum.pop_back();
    }
}

void solve()
{
    cost = IINF;
    VI num(4);
    rep(i, 4)
    {
        cin >> num[i];
    }
    VI curnum;
    dfs(curnum, 0, num);
    rep(i, 4)
    {
        if (ans[i] != 0)
        {
            cout << value[i] << " " << ans[i] << endl;
        }
    }
}

