            }
            else
            {
                get<0>(coin) += m / 10;
                m -= get<0>(coin) * 10;
                break;
            }
        }
        while (fifty >= 2)
        {
            if (m >= 100)
            {
                get<1>(coin) += 2;
                fifty -= 2;
                m -= 100;
            }
            else if (m == 50)
            {
                get<1>(coin) += 1;
                m -= get<1>(coin) * 50;
                break;
            }
            else
