        {
            get<2>(coin) += m / 100 % 5;
            one_hundred -= m / 100 % 5;
            m -= get<2>(coin) * 100;
        }
        while (one_hundred >= 5)
        {
            if (m >= 500)
            {
                get<2>(coin) += 5;
                one_hundred -= 5;
                m -= 500;
            }
