  for(int i = 0; i < 4; i++ ) {
    a[i] = n / forans[3-i];
    n -= a[i]*forans[3-i];
	num += a[i];
  }
  return num;
}

int solve(int n) {
  int ans[4];
  int input[4];
  int payment, charge,reduction_coins;
  int reduction_coins_max = -1000;
  for(int i = 0; i < 4; i++ ) {
    cin >> input[i];
  }
  for(int i = 0; i < input[0]+1; i++ ) {
    for(int j = 0; j < input[1]+1; j++ ) {
      for(int k = 0; k < input[2]+1; k++ ) {
        for(int m = 0; m < input[3]+1; m++ ) {
		  payment = 500*m + 100*k + 50*j + 10*i;
		  charge = payment - n;
		  if(charge < 0) continue;
		  reduction_coins = i + j + k + m - charge_coins(charge);
		  if(reduction_coins_max < reduction_coins) {
		    ans[0] = i;
			ans[1] = j;
			ans[2] = k;
			ans[3] = m;
			reduction_coins_max = reduction_coins;
		  }
        }
      }
    }
  }

  for(int i = 0; i < 4; i++ ) {
    if(ans[i] != 0) cout << forans[i] << ' ' << ans[i] << endl;
  }
  return 0;
}

int main() {
  while(cin >> N && N != 0) {
    solve(N);
    cout << endl;
  }
}

