int otsuri();

int shiharai, daikin, otsuri_mai;
int money[4] = {10, 50, 100, 500};

int main()
{
	while(1){
		cin >> daikin;
		if(daikin == 0) break;
		int sum_money = 0, sum_had_mai = 0;
		int had_mai[4], use_mai[4];
		for(int i = 0; i < 4; i++){
			cin >> had_mai[i];
			sum_money = money[i] * had_mai[i];
			sum_had_mai += had_mai[i];
		}
		int sum_have_mai, min_have_mai = 100;
		for(int i = 0; i <= had_mai[3]; i++){
			if(daikin <= 500 * (i - 1)) break;
			for(int j = 0; j <= had_mai[2]; j++){
				for(int k = 0; k <= had_mai[1]; k++){
					for(int l = 0; l <= had_mai[0]; l++){
						shiharai = 500 * i + 100 * j + 50 * k + 10 * l;
						if(shiharai >= daikin) {
							sum_have_mai = sum_had_mai;
							sum_have_mai -= i + j + k + l;
							otsuri_mai = otsuri();
							sum_have_mai += otsuri_mai;
							if(sum_have_mai < min_have_mai){
								min_have_mai = sum_have_mai;
								use_mai[3] = i;
								use_mai[2] = j;
								use_mai[1] = k;
								use_mai[0] = l;
							}
						}
					}
				}
			}
		}
		for(int i = 0; i < 4; i++){
			if(use_mai[i] != 0) cout << money[i] << ' ' << use_mai[i] << endl;
		}
		cout << endl;
	} 
	return 0;
}

int otsuri()
{
	int tsuri = shiharai - daikin;
	int cnt = 0;
	int mai = 0;
	for(int i = 3; i >= 0; i--){
		mai = tsuri / money[i];
		tsuri -= money[i] * mai;
		cnt += mai;
	}
	return cnt;
}

