using namespace std;
typedef pair <string,int> P;
map <string,P> mp;

string find(string x){
  if(mp[x].f!=x) mp[x] = mp[find(mp[x].f)];
  return mp[x].f;
}

void unite(string x,string y){
  mp[x]=mp[find(x)],mp[y]=mp[find(y)];
  if(mp[x].s<mp[y].s) swap(mp[x],mp[y]);
  mp[x] = mp[y];
}

