  return 0;
}

#include<bits/stdc++.h>
using namespace std;

int change(int n, int t, int f, int h, int fh){
    int sum = 10*t + 50*f + 100 * h + 500 * fh;
    if(sum < n)return -1;
    int c = sum - n, rt = 0, rf = 0, rh = 0, rfh = 0;
    if(c >= 500){
        rfh += c / 500;
        if(rfh > 0 && fh > 0)return -1;
        c -= rfh * 500;
    }
    if(c >= 100){
        rh += c / 100;
        if(rh > 0 && h > 0)return -1;
        c -= rh * 100;
    }
    if(c >= 50){
        rf += c / 50;
        if(rf > 0 && f > 0)return -1;
        c -= rf * 50;
    }
    if(c >= 10){
        rt += c / 10;
        if(rt > 0 && t > 0)return -1;
        c -= rt * 10;
    }
    assert(c == 0);
    assert(10*rt + 50*rf + 100*rh + 500*rfh == sum - n);
    return rt + rf + rh + rfh;
}

int main(){
    int n;
    bool flag = false;
    while(cin >> n, n != 0){
        if(flag)cout << endl;
        flag = true;
        int ten, fifty, hundred, five_hundred;
        cin >> ten >> fifty >> hundred >> five_hundred;
        int at = 0, af = 0, ah = 0, afh = 0, sum = INT_MAX;
        for(int t=0;t<=ten;++t){
            for(int f=0;f<=fifty;++f){
                for(int h=0;h<=hundred;++h){
                    for(int fh=0;fh<=five_hundred;++fh){
                        int res = change(n, t, f, h, fh);
                        if(res == -1)continue;
                        res += (ten-t+fifty-f+hundred-h+five_hundred-fh);
                        if(res < sum){
                            at = t;
                            af = f;
                            ah = h;
                            afh = fh;
                            sum = res;
                        }
                    }
                }
            }
        }
        if(at > 0)cout << "10 " << at << endl;
        if(af > 0)cout << "50 " << af << endl;
        if(ah > 0)cout << "100 " << ah << endl;
        if(afh > 0)cout << "500 " << afh << endl;
    }

    return 0;
}

