void otsuri(int seikyu,int *ten, int *fif, int *hund, int *f_hund)
{

	seikyu = abs(seikyu);


	*f_hund = seikyu/500;
	seikyu -= (*f_hund)*500;
	*hund = seikyu/100;
	seikyu -= (*hund)*100;
	*fif = seikyu/50;
	seikyu -= (*fif)*50;
	*ten = seikyu/10;
	seikyu -= (*ten)*10;

}

