int V, all, mini;
int used[4], ans[4], rel[4];
vector< int > p(4);

int remain(int v) {
    int ret = 0;
    ret += v / 500; v %= 500;
    ret += v / 100; v %= 100;
    ret += v / 50;  v %= 50;
    ret += v / 10;
    return ret;
}

void Update(int *used, int sum) {
    int coins = (all - (used[0] + used[1] + used[2] + used[3])) + remain(sum - V);
    if(coins < mini) {
        for(int i = 0; i < 4; i++)
            ans[i] = used[i];
        mini = coins;
    }
}

void rec(int idx, int sum) {
    //cout << idx << " " << sum << endl;
    if(sum >= V) {
        Update(used, sum);
    }
    if(idx > 3) return;
    rec(idx + 1, sum);
    for(int i = 1; i <= p[idx]; i++) {
        used[idx] += i;
        rec(idx + 1, sum + rel[idx] * i);
        used[idx] -= i;
    }
}

