        mini = INF, all = 0;
        for(int i = 3; i >= 0; i--) {
            cin >> p[i]; all += p[i];
        }
        rec(0, 0);

        for(int i = 3; i >= 0; i--)
            if(ans[i]) cout << rel[i] << " " << ans[i] << endl;
    }
}

