	while ((p - 500) >= 0) {
		p -= 500;
		cnt++;
	}
	while ((p - 100) >= 0) {
		p -= 100;
		cnt++;
	}
	while ((p - 50) >= 0) {
		p -= 50;
		cnt++;
	}
	while ((p - 10) >= 0) {
		p -= 10;
		cnt++;
	}
	return cnt;
}

