int main(void) {
    int money;
    int sum;
    int notpay;
    cin >> money;
    int have[4];
    int notpay_num[4];
    int cost[4] = {1, 5, 10, 50};
    while (1) {
        money /= 10;
        for (int i = 0; i < 4; i++) {
            cin >> have[i];
        }
        sum = have[0] + 5 * have[1] + 10 * have[2] + 50 * have[3];
        notpay = sum - money;
        //cout << sum << " " << notpay << endl;
        for (int i = 3; i >= 0; i--) {
        //cout << "NOTPAY = " << notpay << endl;
           notpay_num[i] = notpay / cost[i];
           //cout << notpay_num[i] << endl;
           notpay -= cost[i] * notpay_num[i];
        }
        //cout << "HERE" << endl;
        output(have, notpay_num);

        cin >> money;
        if (money == 0) {
            break;
        }
        cout << endl;
    }
    return 0;
}

