#include <iostream>

using namespace std;

int min1;
int ans[4];

int returnPay(int cost,int accept){
	if(cost>accept)return 10000000;

	int num = accept - cost;

	int cnt=0;

	int data[4] ={500,100,50,10};
	for(int i=0;i<4;i++){
		while(num>=data[i]){
			cnt++;
			num-=data[i];
		}
	}

	return cnt;
}

void makePattern(int *state,int *data,int pos,int n,int cost){

	int okane[4]={500,100,50,10};

	if(pos==n){
		int sum=0;
		for(int i=0;i<n;i++){
			sum+=state[n-1-i]*okane[i];
	//		cout<<state[i]<<" ";
		}
	//	cout<<endl;
	//	cout<<"sum= "<<sum<<endl;

		int tmp=returnPay(cost,sum);
	//	cout<<"tmp= "<<tmp<<endl;
		if(tmp<=min1){
			min1=tmp;
			for(int i=0;i<n;i++){
				ans[i]=state[i];
			}
		}
	}


	else{
	for(int i=0;i<=data[pos];i++){
		state[pos]=i;
		makePattern(state,data,pos+1,n,cost);
	}
	}
}



