const VI coin{10, 50, 100, 500};
void solve(int n) {
    VI cnum(4), ans(4, 0), csum(4, 0);
    int coinnum = 0, sum = 0;
    REP(i, 4) {
        cin >> cnum[i];
        coinnum += cnum[i];
        csum[i] = cnum[i] * coin[i];
        sum += csum[i];
    }
    int amari = sum - n;
    RREP(i, 4) {
        int buf = amari / coin[i];
        amari %= coin[i];
        ans[i] = cnum[i] - buf;
    }
    REP(i, 4) {
        if (ans[i] > 0) cout << coin[i] << " " << ans[i] << endl;
    }
    return;
}

