    if(flg) cout << endl;
    for(int i = 0 ; i < 4 ; i++) cin >> a[i];
    judge();
    for(int i = 0 ; i < 4 ; i++){
      if(ans_coin[i]){
	cout << coin[i] << " " << ans_coin[i] << endl;
      }
    }
    flg = true;
  }

  return 0;
}

