template<typename V>
constexpr string join(const V &v, const string &sep) {
  stringstream ss;
  if (v.size()) ss << v[0];
  loop(i, 1, v.size()) ss << sep << v[i];
  return ss.str();
}

void solve() {
  int x;
  vs a;
  while (cin >> x, x) {
    vi v(4);
    rep(i, 4) cin >> v[3 - i];
    vi t  = {500, 100, 50, 10};
    int s = 0;
    rep(i, 4) s += v[i] * t[i];
    s -= x;
    rep(i, 4) while (s >= t[i]) s -= t[i], v[i]--;
    reverse(all(t)), reverse(all(v));
    stringstream ss;
    rep(i, 4) if (v[i] > 0) ss << t[i] << ' ' << v[i] << endl;
    a.pb(ss.str());
  }
  cout << join(a, "\n");
}

