using namespace std;

int payment(vector<int> p){
	  return 10*p[0] + 50*p[1] + 100*p[2] + 500*p[3];
}
vector<int> charge(int m){
	  vector<int> ans(4);
	  ans[3] = m/500; m -= (m/500)*500;
	  ans[2] = m/100; m -= (m/100)*100;
	  ans[1] = m/50;  m -= (m/50)*50;
	  ans[0] = m/10;
	  return ans;
}
vector<int> _minus(vector<int> a, vector<int> b){
	  vector<int> ans(4);
	  for(int i=0; i<4; i++)
			ans[i] = a[i] - b[i];
	  return ans;
}
vector<int> _plus(vector<int> a, vector<int> b){
	  vector<int> ans(4);
	  for(int i=0; i<4; i++)
			ans[i] = a[i] + b[i];
	  return ans;
}
bool judge(vector<int>& f, vector<int>& ch ){
	  for(int i=0; i<4; i++)
			if( f[i] == 1 and ch[i] > 0)
				  return false;
	  return true;
}

int main(){
	  while(1){
			int N; cin >> N;
			if(!N) break;

			vector<int> c(4);
			int sum=0;;
			for(int i=0; i<4; i++){
				  cin >> c[i];
				  sum += c[i];
			}
			vector<int> f(4,0);
			int mini = 1e+9;
			vector<int> pay(4);
			vector<int> ans(4);
			for(pay[0]=0; pay[0]<=c[0]; pay[0]++){
				  if(pay[0]>0) f[0] = 1;
