vector<map<int,int> > ans;

int mon[] = {10,50,100,500};
int ccount(map<int,int> co,int f);

int main(){
	while(1){
		int fee;
		cin >> fee;
		if(fee == 0) break;

		map<int,int> coin,ucoin,acoin;

		int count = 0;
		for(int i = 0;i < 4;i++){
			int n;
			cin >> n;
			count += n;
			coin[mon[i]] = n;
			ucoin[mon[i]] = 0;
		}
		int tans = 1e9;
		for(int i= 0;i <= coin[mon[0]];i++){
			ucoin[mon[0]] = i;
			for(int j = 0;j <= coin[mon[1]];j++){
				ucoin[mon[1]] = j;
				for(int k = 0;k <= coin[mon[2]];k++){
					ucoin[mon[2]] = k;
					for(int l = 0;l <= coin[mon[3]];l++){
						ucoin[mon[3]] = l;
						int ucount = i + j + k + l;
						int lcount = count - ucount + ccount(ucoin,fee);
						if(tans > lcount){
							tans = lcount;
							acoin = ucoin;
						}
					}
				}
			}
		}

		ans.push_back(acoin);
	}

	for(int i = 0;i < ans.size();i++){
		if(i != 0) cout << endl;
		for(int j = 0;j < 4;j++){
			if(ans[i][mon[j]] != 0) cout << mon[j] << " " << ans[i][mon[j]]<< endl;
		}
	}
}

int ccount(map<int,int> co,int f){
	int am = 0;
	for(int i = 0;i < 4;i++){
		am += co[mon[i]] * mon[i];
	}

	int cm = 0;
	if(am >= f){
		cm = am - f;
	}else{
		return 1e9;
	}

	int cnt = 0;
	for(int i = 3;i >= 0;i--){
		int tn = cm / mon[i];
		cnt += tn;
		cm -= tn * mon[i];
	}

	return cnt;
}

