  bool f = false;
  while(cin >>N && N){
    if(f) cout <<endl;
    f = true;
    ans = INF;
    vector<int> v(T), used(T, 0);
    REP(i, T) cin >>v[T - 1 - i];
    set< vector<int> > closed;
    dfs(v, used, 0, closed);
    //REP(i, T) if(ans_v[i]) cout <<c[i] <<" " <<ans_v[i] <<endl;
    for(int i = T - 1; i >= 0; --i) if(ans_v[i]) cout <<c[i] <<" " <<ans_v[i] <<endl;
  }
  return 0;
}

