int main(){
	while(1){
		int fee;
		cin >> fee;
		if(fee == 0) break;

		map<int,int> coin,ucoin;

		for(int i = 0;i < 4;i++){
			int n;
			cin >> n;
			coin[mon[i]] = n;
			ucoin[mon[i]] = 0;
		}

		map<int,int> dp[4][20];
		dp[0][0] = ucoin;
		for(int i = 0;i < 4;i++){
			for(int j = 0;j < coin[mon[i]];j++){
				ucoin = dp[i][j];
				ucoin[mon[i]]++;
				if(ccount(dp[i][j],fee) >= ccount(ucoin,fee)){
					dp[i][j+1] = ucoin;
				}else{
					dp[i][j+1] = dp[i][j];
				}
			}
			dp[i+1][0] = dp[i][coin[mon[i]]];
		}

		ans.push_back(dp[3][coin[mon[3]]]);
	}

	for(int i = 0;i < ans.size();i++){
		for(int j = 0;j < 4;j++){
			if(ans[i][mon[j]] != 0) cout << mon[j] << " " << ans[i][mon[j]]<< endl;
		}cout << endl;
	}
}

int ccount(map<int,int> co,int f){
	int am = 0;
	for(int i = 0;i < 4;i++){
		am += co[mon[i]] * mon[i];
	}

	int cm = 0;
	if(am > f){
		cm = am - f;
	}else{
		cm = f - am; 
	}

	int cnt = 0;
	for(int i = 3;i >= 0;i--){
		int tn = cm / mon[i];
		cnt += tn;
		cm -= tn * mon[i];
	}

	return cnt;
}

