		t_num mint={100,100,100,100};
		t_num ans;


		for(int i=0;i<=e500;i++){
			for(int j=0;j<=e100;j++){
				for(int k=0;k<=e50;k++){
					for(int l=0;l<=e10;l++){
						t_num watasu={l,k,j,i};
						if(n>watasu.summoney())continue;
						t_num ttemoto=mai-watasu;
						if(mint.sumn()>nturi(watasu.summoney()-n).sumn()+ttemoto.sumn()){
							mint=nturi(watasu.summoney()-n)+ttemoto;
							ans=watasu;
						}
					}
				}
			}
		}
		if(ans.m10)cout<<10<<' '<<ans.m10<<endl;
