using namespace std;

struct ANS{
	int pay_10,pay_50,pay_100,pay_500,toal;
};

int main(){

	int price,num_10,num_50,num_100,num_500,tmp,pay_sum;
	int ret_10,ret_50,ret_100,ret_500;
	ANS ans;
	bool First = true;

	while(true){
		scanf("%d",&price);
		if(price == 0)break;

		ans.toal = 10000000;

		scanf("%d %d %d %d",&num_10,&num_50,&num_100,&num_500);

		for(int a = 0; a <= num_10; a++){
			for(int b = 0; b <= num_50; b++){
				for(int c = 0; c <= num_100; c++){
					for(int d = 0; d <= num_500; d++){
						if((pay_sum = 10*a+50*b+100*c+500*d) >= price){

							ret_10 = ret_50 = ret_100 = ret_500 = 0;

							pay_sum -= price;
							if(pay_sum >= 500){
								ret_500 = pay_sum/500;
								pay_sum %= 500;
							}
							if(pay_sum >= 100){
								ret_100 = pay_sum/100;
								pay_sum %= 100;
							}
							if(pay_sum >= 50){
								ret_50 = pay_sum/50;
								pay_sum %= 50;
							}
							if(pay_sum >= 10){
								ret_10 = pay_sum/10;
								pay_sum %= 10;
							}

							if((a > 0 && ret_10 > 0)||(b > 0 && ret_50 > 0)||(c > 0 && ret_100 > 0)||(d > 0 && ret_500 > 0)){
								//Do nothing
							}else{
								tmp = num_10 + num_50 + num_100 + num_500 - (a+b+c+d-(ret_10+ret_50+ret_100+ret_500));
								if(tmp < ans.toal){
									ans.toal = tmp;
									ans.pay_10 = a;
									ans.pay_50 = b;
									ans.pay_100 = c;
									ans.pay_500 = d;
								}
							}
						}
					}
				}
			}
		}

		if(!First)printf("\n");

		if(ans.pay_10 > 0)printf("10 %d\n",ans.pay_10);
		if(ans.pay_50 > 0)printf("50 %d\n",ans.pay_50);
		if(ans.pay_100 > 0)printf("100 %d\n",ans.pay_100);
		if(ans.pay_500 > 0)printf("500 %d\n",ans.pay_500);

		First = false;
	}
	return 0;
}

