int m[]={10,50,100,500};
int culc_money(int a,int b,int c,int d){
    return a*10+b*50+c*100+d*500;
}
vector<int> make_change(int a,int b,int c,int d,int n){
    vector<int> ret(4);
    int change_sum=culc_money(a,b,c,d)-n;
    for(int i=3;i>=0;i--){
        ret[i]=change_sum/m[i];
        change_sum-=m[i]*ret[i];
    }
    return ret;
}

signed main (){
    int n;
    bool start=true;
    while(1){

        cin>>n;
        if(n==0)break;
        if(!start)cout<<endl;
        else start=false;
        vector<int> v(4),ans(4);
        rep(i,0,4)cin>>v[i];
        int ans_ct=1e9;
        rep(i,0,v[0]+1){
            rep(j,0,v[1]+1){
                rep(k,0,v[2]+1){
                    rep(p,0,v[3]+1){
                        if(culc_money(i,j,k,p)<n)continue;
                        vector<int> change=make_change(i,j,k,p,n);
                        //
                        if(change[0]&&i)continue;
                        if(change[1]&&j)continue;
                        if(change[2]&&k)continue;
                        if(change[3]&&p)continue;

                        change[0]+=v[0]-i;
                        change[1]+=v[1]-j;
                        change[2]+=v[2]-k;
                        change[3]+=v[3]-p;

                        int ct=accumulate(all(change),0);
                        if(ans_ct>ct){
                            ans_ct=ct;
                            ans[0]=i;
                            ans[1]=j;
                            ans[2]=k;
                            ans[3]=p;
                        }
                    }
                }
            }
        }
        rep(i,0,4){
            if(ans[i]!=0)cout<<m[i]<<" "<<ans[i]<<endl;
        }
    }



}
