                        {
                            used_coin = i + j + k + l;
                            used_coin -= change / 500;
                            change = change % 500;
                            used_coin -= change / 100;
                            change = change % 100;
                            used_coin -= change / 50;
                            change = change % 50;
                            used_coin -= change / 10;
                            if (used_coin > max)
                            {
                                get<0>(coin) = i;
                                get<1>(coin) = j;
                                get<2>(coin) = k;
                                get<3>(coin) = l;
                                max = used_coin;
                            }
                            change = 0;
                            used_coin = 0;
                        }
                    }
                }
            }
        }
        answer.push_back(coin);
    }
    for (int i = 0; i < answer.size(); i++)
