#define FOR(i,a,b) for(int i=(a);i<(b);++i)
#define REP(i,n)  FOR(i,0,n)
#define EPS 1e-8

int calc(int k){
	int ret=0;
	ret+=k/500;
	k%=500;
	ret+=k/100;
	k%=100;
	ret+=k/50;
	k%=50;
	ret+=k/10;
	return ret;
}

int main(){
	int t=0;
	while(1){
		int x,a,b,c,d,A,B,C,D,ret=100;
		cin>>x;
		if(x==0)return 0;
		cin>>a>>b>>c>>d;
		REP(i,a+1){
			REP(j,b+1){
				REP(k,c+1){
					REP(l,d+1){
						int m = i*10+j*50+k*100+l*500;
						if(m<x)continue;
						int tmp=calc(m-x);
						tmp += a-i+b-j+c-k+d-l;
						if(tmp < ret){
							ret = tmp;
							A=i;
							B=j;
							C=k;
							D=l;
						}
					}
				}
			}
		}
		if(t)cout<<endl;
		if(A!=0)cout << 10 << " " << A << endl;
		if(B!=0)cout << 50 << " " << B << endl;
		if(C!=0)cout << 100 << " " << C << endl;
		if(D!=0)cout << 500 << " " << D << endl;
		t++;
	}
}

